import React from 'react';
import { Play, AlertCircle, CheckCircle } from 'lucide-react';
import { motion } from 'framer-motion';

const AnalysisPanel = ({ isAnalyzing, onAnalyze, results }) => {
  return (
    <motion.div
      initial={{ opacity: 0, x: 20 }}
      animate={{ opacity: 1, x: 0 }}
      className="card p-6"
    >
      <h3 className="text-xl font-semibold mb-6 text-gray-800">Analysis Control</h3>

      <div className="space-y-6">
        <motion.button
          whileHover={{ scale: 1.02 }}
          whileTap={{ scale: 0.98 }}
          onClick={onAnalyze}
          disabled={isAnalyzing}
          className="w-full bg-gradient-to-r from-indigo-600 to-purple-600 text-white py-4 rounded-2xl font-semibold text-lg shadow-2xl disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-3"
        >
          {isAnalyzing ? (
            <>
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white"></div>
              Analyzing...
            </>
          ) : (
            <>
              <Play className="h-6 w-6" />
              Start Analysis
            </>
          )}
        </motion.button>

        {results && (
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className={`p-4 rounded-xl ${
              results.matchFound 
                ? 'bg-red-50 border border-red-200' 
                : 'bg-green-50 border border-green-200'
            }`}
          >
            <div className="flex items-center gap-3">
              {results.matchFound ? (
                <AlertCircle className="h-8 w-8 text-red-600" />
              ) : (
                <CheckCircle className="h-8 w-8 text-green-600" />
              )}
              <div>
                <h4 className="font-semibold text-gray-800">
                  {results.matchFound ? 'Match Found!' : 'No Match Found'}
                </h4>
                <p className="text-gray-600">
                  {results.matchFound 
                    ? 'Criminal identified in the video' 
                    : 'No criminal matches found'
                  }
                </p>
              </div>
            </div>
          </motion.div>
        )}
      </div>
    </motion.div>
  );
};

export default AnalysisPanel;
