import React, { useState } from 'react';
import { useCriminal } from '../../context/CriminalContext';
import UploadZone from '../../components/UploadZone/UploadZone';
import AnalysisPanel from '../../components/AnalysisPanel/AnalysisPanel';
import Results from '../../components/Results/Results';
import Timeline from '../../components/Timeline/Timeline';
import { motion } from 'framer-motion';

const Analysis = () => {
  const { criminals, setAnalysisResults } = useCriminal();
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [results, setResults] = useState(null);
  const [backendStatus, setBackendStatus] = useState('unknown');

  const testBackendConnection = async () => {
    try {
      setBackendStatus('checking');
      const response = await fetch('http://localhost:5000/api/health');
      const data = await response.json();
      console.log('Backend connection:', data);
      setBackendStatus('connected');
      alert(`âœ… Backend is running!\nPort: ${data.port}\nStatus: ${data.status}\nMethod: ${data.method || 'OpenCV Face Detection'}`);
    } catch (error) {
      console.error('Backend connection failed:', error);
      setBackendStatus('disconnected');
      alert('âŒ Cannot connect to ML backend.\n\nPlease make sure:\n1. Python server is running on port 5000\n2. Backend dependencies are installed\n3. No firewall blocking the connection');
    }
  };

  const analyzeMedia = async () => {
    if (!selectedImage || !selectedVideo) {
      alert('Please upload both a reference face image and a video to analyze');
      return;
    }

    // Validate file types
    if (!selectedImage.type.startsWith('image/')) {
      alert('Please upload a valid image file for the reference face');
      return;
    }

    if (!selectedVideo.type.startsWith('video/')) {
      alert('Please upload a valid video file for analysis');
      return;
    }

    setIsAnalyzing(true);
    setResults(null);

    try {
      console.log('Starting ML face classification analysis...');
      console.log('Reference image:', selectedImage.name, selectedImage.size, 'bytes');
      console.log('Video file:', selectedVideo.name, selectedVideo.size, 'bytes');

      // Step 1: Upload reference image to Python backend for ML training
      const imageFormData = new FormData();
      imageFormData.append('image', selectedImage);

      console.log('ðŸ“· Training ML classifier on reference face...');
      const imageResponse = await fetch('http://localhost:5000/api/upload-reference', {
        method: 'POST',
        body: imageFormData,
      });

      if (!imageResponse.ok) {
        throw new Error(`HTTP error! status: ${imageResponse.status}`);
      }

      const imageResult = await imageResponse.json();
      console.log('ML training result:', imageResult);

      if (!imageResult.success) {
        throw new Error(imageResult.error || 'Failed to train ML classifier on reference face');
      }

      // Step 2: Analyze video with ML classification
      const videoFormData = new FormData();
      videoFormData.append('video', selectedVideo);

      console.log('ðŸŽ¥ Analyzing video with ML face classification...');
      const videoResponse = await fetch('http://localhost:5000/api/analyze-video', {
        method: 'POST',
        body: videoFormData,
      });

      if (!videoResponse.ok) {
        throw new Error(`HTTP error! status: ${videoResponse.status}`);
      }

      const videoResult = await videoResponse.json();
      console.log('ML classification results:', videoResult);

      if (videoResult.error) {
        throw new Error(videoResult.error);
      }

      // Format results for frontend display with ACCURATE statistics
      const formattedResults = {
        matchFound: videoResult.matchFound,
        criminal: {
          name: "Target Person",
          crime: "Reference Face Match",
          severity: "High",
          image: URL.createObjectURL(selectedImage)
        },
        timestamps: videoResult.timestamps || [],
        analysisTime: videoResult.analysisTime || 'ML Analysis Complete',
        totalFrames: videoResult.totalFrames || 0,
        // ACCURATE ML statistics
        mlStats: {
          totalFacesDetected: videoResult.totalFacesDetected || 0,      // All faces in video
          targetDetections: videoResult.targetDetections || 0,          // Only target person (high confidence)
          possibleDetections: videoResult.possibleDetections || 0,      // Possible matches (medium confidence)
          confidenceThreshold: videoResult.confidenceThreshold || 70,
          accuracyNote: videoResult.accuracyNote || "Only high-confidence matches (>70%) counted as target",
          analysisMethod: videoResult.method || "OpenCV + Feature Matching"
        }
      };

      setResults(formattedResults);
      setAnalysisResults(formattedResults);

      console.log('âœ… ML analysis completed successfully!');
      console.log('Target person found:', formattedResults.matchFound);
      console.log('True target detections:', formattedResults.mlStats.targetDetections);
      console.log('Total faces in video:', formattedResults.mlStats.totalFacesDetected);

    } catch (error) {
      console.error('âŒ Analysis error:', error);

      if (error.message.includes('Failed to fetch')) {
        alert(`ðŸš¨ Connection Error: Cannot reach ML backend\n\nPlease ensure:\n1. Python server is running on port 5000\n2. Run "python3 app.py" in backend folder\n3. Check firewall settings\n\nError: ${error.message}`);
      } else if (error.message.includes('face')) {
        alert(`ðŸ¤– ML Analysis Error: ${error.message}\n\nPlease upload a clear face image where the face is visible and well-lit.`);
      } else if (error.message.includes('video')) {
        alert(`ðŸŽ¥ Video Error: ${error.message}\n\nPlease check the video file is supported (MP4, MOV, AVI) and not corrupted.`);
      } else {
        alert(`âŒ Analysis Failed: ${error.message}`);
      }
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getBackendStatusColor = () => {
    switch (backendStatus) {
      case 'connected': return 'bg-green-500';
      case 'disconnected': return 'bg-red-500';
      case 'checking': return 'bg-yellow-500';
      default: return 'bg-gray-500';
    }
  };

  const getBackendStatusText = () => {
    switch (backendStatus) {
      case 'connected': return 'ML Backend Connected';
      case 'disconnected': return 'ML Backend Offline';
      case 'checking': return 'Checking Connection...';
      default: return 'Check ML Backend';
    }
  };

  return (
    <div className="min-h-screen p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header Section */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center mb-8"
        >
          <h1 className="text-4xl font-bold text-white mb-4">Accurate ML Face Analysis</h1>
          <p className="text-white/80 mb-4 text-lg">
            Powered by OpenCV + Feature Matching (High Accuracy)
          </p>

          {/* Backend Status */}
          <div className="flex justify-center items-center gap-4 mb-4">
            <div className={`w-3 h-3 rounded-full ${getBackendStatusColor()} animate-pulse`}></div>
            <button 
              onClick={testBackendConnection}
              className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg font-semibold transition-colors flex items-center gap-2"
            >
              <span>{getBackendStatusText()}</span>
            </button>
          </div>

          {/* Accuracy Info Cards */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 max-w-4xl mx-auto mb-6">
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 text-white">
              <div className="text-lg font-semibold">ðŸŽ¯ High Accuracy</div>
              <div className="text-sm text-white/80">70%+ confidence threshold</div>
            </div>
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 text-white">
              <div className="text-lg font-semibold">ðŸ‘¤ Target Only</div>
              <div className="text-sm text-white/80">Counts only reference person</div>
            </div>
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 text-white">
              <div className="text-lg font-semibold">ðŸ” Feature Matching</div>
              <div className="text-sm text-white/80">Multi-method comparison</div>
            </div>
          </div>
        </motion.div>

        {/* Main Analysis Area */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Upload Section */}
          <div className="space-y-6">
            <UploadZone
              type="image"
              onFileUpload={setSelectedImage}
              selectedFile={selectedImage}
              onClear={() => setSelectedImage(null)}
            />
            <UploadZone
              type="video"
              onFileUpload={setSelectedVideo}
              selectedFile={selectedVideo}
              onClear={() => setSelectedVideo(null)}
            />

            {/* Upload Status */}
            {(selectedImage || selectedVideo) && (
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="card p-4"
              >
                <h4 className="font-semibold text-gray-800 mb-2">Ready for Accurate ML Analysis</h4>
                <div className="space-y-2 text-sm">
                  {selectedImage && (
                    <div className="flex justify-between">
                      <span className="text-gray-600">Reference Face:</span>
                      <span className="text-green-600 font-medium">âœ“ Ready</span>
                    </div>
                  )}
                  {selectedVideo && (
                    <div className="flex justify-between">
                      <span className="text-gray-600">Analysis Video:</span>
                      <span className="text-green-600 font-medium">âœ“ Ready</span>
                    </div>
                  )}
                  {selectedImage && selectedVideo && (
                    <div className="pt-2 border-t border-gray-200">
                      <div className="text-blue-600 font-medium">
                        âœ… Both files ready for accurate ML classification
                      </div>
                      <div className="text-xs text-gray-500 mt-1">
                        Will only count high-confidence matches (>70%)
                      </div>
                    </div>
                  )}
                </div>
              </motion.div>
            )}
          </div>

          {/* Analysis Panel */}
          <AnalysisPanel
            isAnalyzing={isAnalyzing}
            onAnalyze={analyzeMedia}
            results={results}
            hasFiles={selectedImage && selectedVideo}
          />
        </div>

        {/* Results Section */}
        {results && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="space-y-6"
          >
            {/* ACCURATE Statistics Cards */}
            <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
              <div className="card p-4 text-center">
                <div className="text-2xl font-bold text-blue-600">{results.mlStats?.totalFacesDetected || 0}</div>
                <div className="text-sm text-gray-600">Total Faces in Video</div>
                <div className="text-xs text-gray-400">All people detected</div>
              </div>
              <div className="card p-4 text-center bg-green-50 border border-green-200">
                <div className="text-2xl font-bold text-green-600">{results.mlStats?.targetDetections || 0}</div>
                <div className="text-sm text-gray-600">True Target Matches</div>
                <div className="text-xs text-gray-400">Reference person only</div>
              </div>
              <div className="card p-4 text-center bg-yellow-50 border border-yellow-200">
                <div className="text-2xl font-bold text-yellow-600">{results.mlStats?.possibleDetections || 0}</div>
                <div className="text-sm text-gray-600">Possible Matches</div>
                <div className="text-xs text-gray-400">Lower confidence</div>
              </div>
              <div className="card p-4 text-center bg-purple-50 border border-purple-200">
                <div className="text-2xl font-bold text-purple-600">{results.timestamps.length}</div>
                <div className="text-sm text-gray-600">Detection Events</div>
                <div className="text-xs text-gray-400">Timestamps found</div>
              </div>
              <div className="card p-4 text-center bg-orange-50 border border-orange-200">
                <div className="text-2xl font-bold text-orange-600">{results.mlStats?.confidenceThreshold || 70}%</div>
                <div className="text-sm text-gray-600">Confidence Threshold</div>
                <div className="text-xs text-gray-400">Minimum for match</div>
              </div>
            </div>

            {/* Accuracy Summary */}
            {results.mlStats && (
              <div className="card p-6 bg-blue-50 border border-blue-200">
                <h3 className="text-xl font-semibold text-blue-800 mb-3">ðŸŽ¯ Accuracy Report</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                  <div>
                    <div className="flex justify-between mb-2">
                      <span className="text-blue-700">Total faces in video:</span>
                      <span className="font-semibold">{results.mlStats.totalFacesDetected}</span>
                    </div>
