import React, { useState } from 'react';
import { useCriminal } from '../../context/CriminalContext';
import CriminalDatabase from '../../components/CriminalDatabase/CriminalDatabase';
import { Plus, Search } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const Database = () => {
  const { criminals, addCriminal } = useCriminal();
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newCriminal, setNewCriminal] = useState({
    name: '',
    crime: '',
    severity: 'Medium',
    status: 'Wanted',
    image: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face',
    lastSeen: ''
  });

  const filteredCriminals = criminals.filter(criminal =>
    criminal.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    criminal.crime.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleAddCriminal = (e) => {
    e.preventDefault();
    if (newCriminal.name && newCriminal.crime) {
      addCriminal(newCriminal);
      setNewCriminal({
        name: '',
        crime: '',
        severity: 'Medium',
        status: 'Wanted',
        image: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face',
        lastSeen: ''
      });
      setShowAddForm(false);
    }
  };

  return (
    <div className="min-h-screen p-6">
      <div className="max-w-7xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          className="flex justify-between items-center mb-8"
        >
          <div>
            <h1 className="text-4xl font-bold text-white mb-2">Criminal Database</h1>
            <p className="text-white/80">Manage and search criminal records</p>
          </div>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => setShowAddForm(true)}
            className="bg-white text-indigo-600 px-6 py-3 rounded-xl font-semibold flex items-center gap-2 shadow-2xl"
          >
            <Plus className="h-5 w-5" />
            Add Criminal
          </motion.button>
        </motion.div>

        {/* Search Bar */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="card p-4 mb-6"
        >
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
            <input
              type="text"
              placeholder="Search criminals by name or crime..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            />
          </div>
        </motion.div>

        {/* Criminal Database Component */}
        <CriminalDatabase
          criminals={filteredCriminals}
          onEdit={(criminal) => console.log('Edit:', criminal)}
          onDelete={(id) => console.log('Delete:', id)}
        />

        {/* Add Criminal Modal */}
        <AnimatePresence>
          {showAddForm && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50"
              onClick={() => setShowAddForm(false)}
            >
              <motion.div
                initial={{ scale: 0.9, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.9, opacity: 0 }}
                className="bg-white rounded-2xl p-6 w-full max-w-md"
                onClick={(e) => e.stopPropagation()}
              >
                <h3 className="text-xl font-semibold mb-4 text-gray-800">Add New Criminal</h3>
                <form onSubmit={handleAddCriminal} className="space-y-4">
                  <div className="form-group">
                    <label className="form-label">Name</label>
                    <input
                      type="text"
                      value={newCriminal.name}
                      onChange={(e) => setNewCriminal({...newCriminal, name: e.target.value})}
                      className="form-input"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Crime</label>
                    <input
                      type="text"
                      value={newCriminal.crime}
                      onChange={(e) => setNewCriminal({...newCriminal, crime: e.target.value})}
                      className="form-input"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Risk Severity</label>
                    <select
                      value={newCriminal.severity}
                      onChange={(e) => setNewCriminal({...newCriminal, severity: e.target.value})}
                      className="form-input"
                    >
                      <option value="Low">Low</option>
                      <option value="Medium">Medium</option>
                      <option value="High">High</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label className="form-label">Status</label>
                    <select
                      value={newCriminal.status}
                      onChange={(e) => setNewCriminal({...newCriminal, status: e.target.value})}
                      className="form-input"
                    >
                      <option value="Wanted">Wanted</option>
                      <option value="Suspected">Suspected</option>
                      <option value="Monitor">Monitor</option>
                    </select>
                  </div>
                  <div className="flex gap-3 pt-4">
                    <button
                      type="button"
                      onClick={() => setShowAddForm(false)}
                      className="flex-1 py-3 border border-gray-300 text-gray-700 rounded-xl font-medium"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      className="flex-1 py-3 bg-indigo-600 text-white rounded-xl font-medium"
                    >
                      Add Criminal
                    </button>
                  </div>
                </form>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default Database;
